import '../blocks/form/form.css';
import { useState } from 'react';

export default function Form({
  handleSubmitAction,
  buttonContent
}) {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  let handleSubmit = (e) => {
    e.preventDefault();
    console.log('submit');
    console.log(email, password);
    handleSubmitAction({email, password})
  }

  return (
    <form className='form' onSubmit={handleSubmit}>
      <label >E-mail:</label>
      <input type='email' className='form__input' />
      <label >Password:</label>
      <input type='password' className='form__input' minLength={8}/>
      { buttonContent !== 'LOG IN' && <>
      <label >Confirm password:</label>
      <input type='password' className='form__input' />
      </>}
      <button className='button form__btn-submit'>{buttonContent}</button>
    </form>
  )
}
