import '../blocks/button/button.css';

export default function Button({content, handleClick, type, id=null}) {

  const style = (type === 'image') ? 'button button_image' : 'button button_text';

  let onClick = (e) => {
    e.preventDefault();
    handleClick(id);
  }

  return (
    <button 
      className={style}
      onClick={onClick}
    >
      {(type === 'image') ? <img src={content} alt=''></img> : `${content}`}
    </button>
  )
}
