  const [editModeTask, setEditModeTask] = useState(false);
  const {content, id } = task;
  return (
    <div className="task" onClick={handleClick}>
      { editModeTask ?? <textarea>{content}</textarea> }
      { editModeTask || `${content}`} 
      <Button type='delete' id={id} handleClick={handleDeleteTask}/>
    </div>
  )
}
