import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import '../blocks/column/column.css';
import Button from './Button';

export default function Column({id, name, handleClick}) {

  const {setNodeRef, attributes, listeners, transform, transition, isDragging} = useSortable({
    id:id,
    data: {
      type: 'column',
      name: name,
      id:id,
    }
  });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <div className='column' ref={setNodeRef} style={style}>
      <div id={id} className='column__header' {...attributes} {...listeners} >
        <div className='column__header-container'>
          <div>0</div>
          {name}
        </div>

        <Button
          handleClick={handleClick}
          type='image'
          id={id}
        />
      </div>
    <div className='column__content'>...</div>
    <div>footer</div>
    </div>

  )
}
