import { useState } from 'react';
import '../blocks/popup/popup.css';

export default function Popup() {
  const [isOpen, setIsOpen] = useState(false);

  let handleOpenClick = () => {
    setIsOpen(true);
  }
  let handleCloseCLick = () => {
    setIsOpen(false);
  }

  return (
    <>
      <button className="popup__button" onClick={handleOpenClick}>
        i
        <div className="popup__button-div2"></div>
        <div className="popup__button-div1"></div>
      </button>

     { isOpen && <div className="popup" onClick={handleCloseCLick}>
        <div className='popup__content-container' onClick={(e)=> e.stopPropagation()}>
          <p>For working from different devices and synchronization, you need to </p>
          <a href='/' className='popup__link' onClick={(e) => e.preventDefault()}>Log in</a> 
          /
          <a href='/' className='popup__link' onClick={(e) => e.preventDefault()}>Registration</a>
          <form className='popup__form'>
            <label >E-mail:</label>
            <input type='email' />
            <label >Password:</label>
            <input type='password' />
            <button className='button'>LOG-IN</button>
          </form>
        </div>
      </div>}
    </>
  )
}
