// import logo from './logo.svg';
import { useState } from 'react';
import '../blocks/app/app.css';
import Button from './Button';
import Column from './Column';

function App() {

  const [columns, setColumns] = useState(localStorage.getItem('kanban-board') || []);

  let handleClickAddColumn = () => {
    setColumns([...columns, {name: 'Column' + (columns.length + 1), id: Math.ceil(Math.random()*100000)} ]);
    localStorage.setItem('kanban-board', columns);
  }

  return (
    <div className="app">

      {columns.map(el => {
        return <Column
          key={el.id}
          id={el.id}
          name={el.name}
        />
      })}

      <Button 
        text='Add column'
        handleClick={handleClickAddColumn}
      />

    </div>
  );
}

export default App;
