import Button from "./Button";
import '../blocks/task/task.css';
import { useState } from "react";

export default function Task({task, handleDeleteTask}) {
  const [editModeTask, setEditModeTask] = useState(false);
  const {content, id } = task;

  let handleClick = () => {
    setEditModeTask(true)
  }

  return (
    <div className="task" onClick={handleClick}>
      { editModeTask ? <textarea 
      autoFocus
      onBlur={()=>{
        setEditModeTask(false)
        // setInput(name)
      }}
      >{content}</textarea> : `${content}`} 
      <Button type='delete' id={id} handleClick={handleDeleteTask}/>
    </div>
  )
}
