import { useEffect, useMemo, useState } from 'react';
import '../blocks/app/app.css';
import Button from './Button';
import Column from './Column';
import { DndContext, DragOverlay } from '@dnd-kit/core';
import {SortableContext} from '@dnd-kit/sortable';
import { createPortal } from 'react-dom';

function App() {

  const [columns, setColumns] = useState(JSON.parse(localStorage.getItem('kanban-board')) || []);

  let handleClickAddColumn = () => {
    setColumns([...columns, {name: 'Column' + (columns.length + 1), id: Math.ceil(Math.random()*100000)} ]);
    // localStorage.setItem('kanban-board', JSON.stringify(columns));
  }

  let handleClickDeleteColumn = (id) => {
    let x = columns.filter( el => el.id !== id)
    setColumns(x)
  }

  /// dnd-kit 
  const columnsID = useMemo(() => columns.map(el => el.id), [columns]);
  const [activeColumn, setActiveColumn] = useState(null);

  function onDragStart(event) {
    if (event.active.data.current?.type === 'column') {
      setActiveColumn(event.active.data.current.id)
    }
    return
  }
  function onDragEnd(event) {
    if (event.active.data.current?.type === 'column') {
      setActiveColumn(event.active.data.current.id)
    }
    return
  }
  /// dnd-kit end

  useEffect(() => {
    localStorage.setItem('kanban-board', JSON.stringify(columns));
  }, [columns])

  return (
    <DndContext onDragStart={onDragStart} onDragEnd={onDragEnd}>
      <div className="app">
        <SortableContext items={columnsID}>
        {columns.map(el => {
          return <Column
            key={el.id}
            id={el.id}
            name={el.name}
            handleClick={handleClickDeleteColumn}
          />
        })}
        </SortableContext>

        <Button   
          content='Add column'
          handleClick={handleClickAddColumn}
          type='text'
        />

      </div>

      {createPortal(
       <DragOverlay> {activeColumn && <Column />}</DragOverlay>,
      document.body)}
    </DndContext>
  );
}

export default App;
