import '../blocks/button/button.css';

export default function Button({content, handleClick, type}) {

  const style = (type === 'image') ? 'button button_image' : 'button button_text';

  let onClick = (e) => {
    e.preventDefault();
    handleClick();
  }

  return (
    <button 
      className={style}
      onClick={onClick}
    >
      {type === 'image' ? <img url={content} alt=''></img> : {content}}
    </button>
  )
}
