import { useState } from 'react';
import '../blocks/popup/popup.css';

export default function Popup() {
  const [isOpen, setIsOpen] = useState(false);
  const [isOpenLogIn, setIsOpenLogIn] = useState(false);
  const [isOpenNewAccount, setIsOpenNewAccount] = useState(false);

  let handleOpenClick = () => {
    setIsOpen(true);
  }
  let handleCloseCLick = () => {
    setIsOpen(false);
  }
  let handleOpenFormLogIn = (e) => {
    e.preventDefault();
    setIsOpenLogIn(true)
  }
  let handleOpenFormNewAccount = (e) => {
    e.preventDefault();
    setIsOpenNewAccount(true)
  }

  return (
    <>
      <button className="popup__button" onClick={handleOpenClick}>
        i
        <div className="popup__button-div2"></div>
        <div className="popup__button-div1"></div>
      </button>

     { isOpen && <div className="popup" onClick={handleCloseCLick}>
        <div className='popup__content-container' onClick={(e)=> e.stopPropagation()}>
          <p>For working from different devices and synchronization, you need to </p>
          <a href='/' className='popup__link' onClick={handleOpenFormLogIn}>Log in</a> 
          /
          <a href='/' className='popup__link' onClick={handleOpenFormNewAccount}>Registration</a>

          {isOpenLogIn && <form className='popup__form'>
            <label >E-mail:</label>
            <input type='email' />
            <label >Password:</label>
            <input type='password' />
            <button className='button popup__btn-submit'>LOG IN</button>
          </form>}

          {isOpenNewAccount && <form className='popup__form'>
            <label >E-mail:</label>
            <input type='email' />
            <label >Password:</label>
            <input type='password' />
            <label >Confirm password:</label>
            <input type='password' />
            <button className='button popup__btn-submit'>New account</button>
          </form>}

        </div>
      </div>}
    </>
  )
}
