import { useState } from 'react';
import '../blocks/app/app.css';
import Button from './Button';

function App() {

  const [columns, setColumns] = useState([]);

  let handleClickAddColumn = () => {
    setColumns([...columns, {name: columns.length + 1, id: Math.ceil(Math.random()*100000)} ])
    console.log(columns)
  }

  return (
    <div className="app">



      <Button 
        text='Add column'
        handleClick={handleClickAddColumn}
      />

    </div>
  );
}

export default App;
