import { initializeApp } from "firebase/app";
import { getDatabase, ref, set } from "firebase/database";
import {getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword, onAuthStateChanged, signOut} from "firebase/auth";
// TODO: Add SDKs for Firebase products that you want to use
// https://firebase.google.com/docs/web/setup#available-libraries

const firebaseConfig = {
  databaseURL: 'https://kanban-brd-default-rtdb.firebaseio.com/',
  apiKey: "AIzaSyAnbTHf5pbc8du7zHhnGPSkLkVLFcgWSlA",
  authDomain: "kanban-brd.firebaseapp.com",
  projectId: "kanban-brd",
  storageBucket: "kanban-brd.appspot.com",
  messagingSenderId: "42814942618",
  appId: "1:42814942618:web:f44f4b8a8d5be72e679e23"
};

const app = initializeApp(firebaseConfig);
const db = getDatabase(app);
const auth = getAuth(app);

function creatUser({email, password}) {
  return createUserWithEmailAndPassword(auth, email, password)
}

function logInUser({email, password}) {
  return signInWithEmailAndPassword(auth, email, password)
}

// function stateAuth() {
//   return onAuthStateChanged(auth, user => {
//     if (user) return user
//   })
// }

function signOutUser() {
  return signOut(auth)
}

///////
const userID = "CfBCWt55kAewnTTPHm6TpEFjwOw2"
//////

function addNewTask({
  userID, task, columnID, 
}) {
  return set(ref(db, `${userID}/tasks/`), {
    task: task,
    columnID: columnID,
  })
}

function addNewColumn({
  userID, column, 
}) {
  return set(ref(db, `CfBCWt55kAewnTTPHm6TpEFjwOw2/columns/`), {
    column: column,
  })
}

export {
  creatUser,
  logInUser,
  // stateAuth,
  signOutUser,
  addNewColumn,
  addNewTask
};
