import { useEffect, useMemo, useState } from 'react';
import '../blocks/app/app.css';
import Header from './Header';
import Button from './Button';
import Column from './Column';
import Footer from './Footer';
import Popup from './Popup';
import { DndContext, PointerSensor, useSensor, useSensors } from '@dnd-kit/core';
import {SortableContext, arrayMove} from '@dnd-kit/sortable';

import { logIn, logOut, selectUser } from '../context/user';
import { useDispatch, useSelector } from 'react-redux';
import { getAuth, onAuthStateChanged } from "firebase/auth";
import { creatUser, logInUser, signOutUser, getData, addNewColumn } from '../api/firebase';

export default function App() {

  const {user, id} = useSelector(selectUser);  // initially check isAnyUserLogIN

  const [columns, setColumns] = useState([]);
  const [tasks, setTasks] = useState(JSON.parse(localStorage.getItem('kanban-board-tasks')) || []);

  console.log(columns)

  const [isOpen, setIsOpen] = useState(false); /// popup
  const [isOpenLogIn, setIsOpenLogIn] = useState(false);
  const [isOpenNewAccount, setIsOpenNewAccount] = useState(false);

  async function handleClickAddColumn() {
    if (user) {
      addNewColumnDB({userID:id})
    } else {
      await new Promise(setColumns([...columns, {name: 'Column ' + (columns.length + 1), id: Math.ceil(Math.random()*100000)} ]));
      localStorage.setItem('kanban-board-columns', JSON.stringify(columns));
    }
  }

  let handleClickDeleteColumn = (id) => {
    const filteredColumns = columns.filter( el => el.id !== id)
    const filteredTasks = tasks.filter(task => task.idColumn !== id)
    setColumns(filteredColumns)
    setTasks(filteredTasks)
  }

  let updateColumnName = ({id, input}) => {
    const update = columns.map(el => {
      if (el.id !== id)  return el
      return {...el, name:input}
    })
    setColumns(update)
  }

  let handleAddTask = (id) => {
    const newTask = {
      idColumn: id,
      id: Math.ceil(Math.random()*100000),
      content: 'Task ' + (tasks.length + 1),
    }
    setTasks([...tasks, newTask])
  }

  let handleDeleteTask = (id) => {
    const filteredTasks = tasks.filter(task => task.id !== id)
    setTasks(filteredTasks)
  }

  let updateTask = ({id, input}) => {
    const updatedTasks = tasks.map(task => {
      if (task.id !== id) return task
      return {...task, content:input}
    })
    setTasks(updatedTasks)
  }

  /// dnd-kit 
  const columnsID = useMemo(() => columns.map(el => el.id), [columns]);
  const [activeColumn, setActiveColumn] = useState(null);
  const [activeTask, setActiveTask] = useState(null);
  //managing the area from where dnd starts to work, just move for this 'distance', to provide work other buttons
  const sensors = useSensors(useSensor(PointerSensor, {
    activationConstraint: {
      distance: 3,
    }
  }));

  function onDragStart(event) {
    if (event.active.data.current?.type === 'column') {
      setActiveColumn(event.active.data.current.id)
      return
    }
    if (event.active.data.current?.type === 'task') {
      setActiveTask(event.active.data.current.task)
      return
    }
  }
  function onDragEnd(event) {
    setActiveTask(null)
    setActiveColumn(null)

    const {active,over} = event; /// {active, over}

    if (!over) return

    const activeId = active.id;  /// active
    const overId = over.id;

    if ( activeId === overId) return

    setColumns(columns => {
      const draggindColumn = columns.findIndex(el => el.id === activeId)
      const overColumn = columns.findIndex(el => el.id === overId)
      return arrayMove(columns, draggindColumn, overColumn)
    })
  }
  function onDragOver(event) {
    const {active,over} = event; /// {active, over}

    if (!over) return

    const activeId = active.id;  /// active
    const overId = over.id;

    if ( activeId === overId) return

    if (!activeTask) return

    const isActiveTask = active.data.current.type === 'task'
    const isOverTask = over.data.current.type === 'task'
    /// dropping a task over another task
    if (isActiveTask && isOverTask) {
      setTasks(tasks => {
        const activeIndex = tasks.findIndex(el => el.id === activeId)
        const overIndex = tasks.findIndex(el => el.id === overId)

        tasks[activeIndex].idColumn = tasks[overIndex].idColumn

        return arrayMove(tasks, activeIndex, overIndex)
      })
    }
    /// dropping a task over am ampty other column
    const isOverColumn = over.data.current.type === 'column';
    setActiveColumn(null)

    if (isActiveTask && isOverColumn) {
      setTasks(tasks => {
        const activeIndex = tasks.findIndex(el => el.id === activeId)

        tasks[activeIndex].idColumn = overId

        return arrayMove(tasks, activeIndex, activeIndex)
      })
    }
  }
  /// dnd-kit end


  // useEffect(() => {
  //   if (!user) localStorage.setItem('kanban-board-columns', JSON.stringify(columns));
  // }, [columns, user])
  // useEffect(() => {
  //   localStorage.setItem('kanban-board-tasks', JSON.stringify(tasks));
  // }, [tasks])
  const dispatch = useDispatch();
  const auth = getAuth();
  useEffect(() => {
    onAuthStateChanged(auth, (user) => {
      if (user) {
        // User is signed in, see docs for a list of available properties
        // https://firebase.google.com/docs/reference/js/auth.user
        const uid = user.uid;
        const email = user.email;
        dispatch(logIn({email:email,id:uid}))
        setColumns(getInitialDataDB({id:uid, str:'columns'}))
      } else {   // User is signed out
        setColumns(getInitialDataLS('kanban-board-columns'))
      } 
    });
  },[])

  return (
    <DndContext onDragStart={onDragStart} onDragEnd={onDragEnd} onDragOver={onDragOver} sensors={sensors}>

      {user ? <Header handleLogOut={handleLogOut} /> : <Popup 
      handleLogIn={handleLogIn} 
      handleCreatNewAccount={handleCreatNewAccount} 
      handleOpenClick={handleOpenClick} 
      handleCloseCLick={handleCloseCLick} 
      isOpen={isOpen} 
      isOpenLogIn={isOpenLogIn} 
      isOpenNewAccount={isOpenNewAccount} 
      handleOpenFormLogIn={handleOpenFormLogIn} 
      handleOpenFormNewAccount={handleOpenFormNewAccount} />}

      <div className="app">
        <SortableContext items={columnsID}>
        {columns.map(el => {
          return <Column
            key={el.id}
            id={el.id}
            name={el.name}
            handleClick={handleClickDeleteColumn}
            updateColumnName={updateColumnName}
            handleAddTask={handleAddTask}
            tasks={tasks.filter(task => task.idColumn === el.id )}
            handleDeleteTask={handleDeleteTask}
            updateTask={updateTask}
          />
        })}
        </SortableContext>

        <Button   
          content='Add column'
          handleClick={handleClickAddColumn}
          type='add'
        />

      </div>
      {/* patch */} {activeColumn ? <></> : <></>} {/* patch end */}
      <Footer />
    </DndContext>
  );

  function handleCreatNewAccount({email,password}) {
    creatUser({email, password})
    .then(user => {
      console.log(user)
      setIsOpenLogIn(true)
      setIsOpenNewAccount(false)
    })
    .catch(err => {
      console.log(err)
    })
  }
  function handleLogIn({email,password}) {
    logInUser({email, password})
    .then(user => {
      // setColumns([])
      setIsOpenLogIn(false)
      setIsOpen(false)
      if (user.user) {
        localStorage.setItem('kanban-token', JSON.stringify(user.user.accessToken))
        dispatch(logIn({email:email,id:user.user.uid}))
      }
    })
    .catch(err => {
      console.log('catch', err)
    })
  }
  function handleLogOut() {
    signOutUser()
    .then(() => {
      // setColumns([])
      dispatch(logOut())
    })
    .catch()
  }
  function handleOpenClick() {
    setIsOpen(true);
  }
  function handleCloseCLick() {
    setIsOpen(false);
    setIsOpenLogIn(false);
    setIsOpenNewAccount(false);
  }
  function handleOpenFormLogIn(e) {
    e.preventDefault();
    setIsOpenNewAccount(false)
    setIsOpenLogIn(true)
  }
  function handleOpenFormNewAccount(e) {
    e.preventDefault();
    setIsOpenLogIn(false)
    setIsOpenNewAccount(true)
  }

  function getInitialDataDB({id, str}) {  /// 'columns'  'tasks'
    return  getData({userID:id, category:str})
      .then(snapshot => {
        if (snapshot.exists()) {
          console.log(snapshot.val());
          return snapshot.val()
        } else {
          console.log("No data available");
          return []
        }
      })
      .catch() 
  }
  function getInitialDataLS(str) {  /// 'kanban-board-columns' 'kanban-board-tasks'
    return JSON.parse(localStorage.getItem(str));
  }
  function addNewColumnDB({userID, column='column'}) {
    addNewColumn({userID, column})
    .then(snapshot => {
      if (snapshot.exists()) {
        console.log('addNewColumnDB',snapshot.val());
        return snapshot.val()
      } else {
        console.log("No snapshot in addNewColumnDB");
      }
    })
    .catch()
  }
}
