import { useState } from 'react';
import {creatUser, logInUser} from '../api/firebase';
import '../blocks/popup/popup.css';
import Form from './Form';

import { logIn } from '../context/user';
import { useDispatch } from 'react-redux';

export default function Popup() {
  const [isOpen, setIsOpen] = useState(false);
  const [isOpenLogIn, setIsOpenLogIn] = useState(false);
  const [isOpenNewAccount, setIsOpenNewAccount] = useState(false);

  const dispatch = useDispatch();

  let handleOpenClick = () => {
    setIsOpen(true);
  }
  let handleCloseCLick = () => {
    setIsOpen(false);
    setIsOpenLogIn(false);
    setIsOpenNewAccount(false);
  }
  let handleOpenFormLogIn = (e) => {
    e.preventDefault();
    setIsOpenNewAccount(false)
    setIsOpenLogIn(true)
  }
  let handleOpenFormNewAccount = (e) => {
    e.preventDefault();
    setIsOpenLogIn(false)
    setIsOpenNewAccount(true)
  }

  let handleCreatNewAccount = ({email,password}) => {
    creatUser({email, password})
    .then(user => {
      console.log(user)
      setIsOpenLogIn(true)
      setIsOpenNewAccount(false)
    })
    .catch(err => {
      console.log(err)
    })
  }

  let handleLogIn = ({email,password}) => {
    logInUser({email, password})
    .then(user => {
      console.log(user)
      setIsOpenLogIn(false)
      setIsOpen(false)
      if (user.user) {
        localStorage.setItem('kanban-token', JSON.stringify(user.user.accessToken))
        dispatch(logIn({email:email,id:user.user.uid}))
      }
    })
    .catch(err => {
      console.log('catch', err)
    })
  }

  return (
    <>
      <button className="popup__button" onClick={handleOpenClick}>
        i
        <div className="popup__button-div2"></div>
        <div className="popup__button-div1"></div>
      </button>

     { isOpen && <div className="popup" onClick={handleCloseCLick}>
        <div className='popup__content-container' onClick={(e)=> e.stopPropagation()}>
          <p>For working from different devices and synchronization, you need to 
          {' '}
          <a href='/' className='popup__link' onClick={handleOpenFormLogIn}>Log in</a> 
          {' or '}
          <a href='/' className='popup__link' onClick={handleOpenFormNewAccount}>Registration</a>
          </p>

          {isOpenLogIn && <Form buttonContent='LOG IN' handleSubmitAction={handleLogIn} /> }
          {isOpenNewAccount && <Form buttonContent='Create a new account' handleSubmitAction={handleCreatNewAccount} /> }

        </div>
      </div>}
    </>
  )
}
